import classes from './Counter.module.css';
import { useSelector, useDispatch } from 'react-redux';
// import {  useStore } from 'react-redux';

const Counter = () => {

  const dispatch = useDispatch();
  const counterState = useSelector((state) => state.counter) ;
  const showCounter = useSelector((state) => state.showCounter) 
  // console.log(useStore().getState());

  const handleIncrement = (value) => {
    dispatch({type : value ? `INCREASE_BY` : 'INCREASE', payload : value}) 
  }
  const handleDecrement = (value) => {
    dispatch({type : value ? `DECREASE_BY`: 'DECREASE', payload : value}) 
  }

  const toggleCounterHandler = () => {
    dispatch({type: "TOGGLE_COUNTER"})
  };

  return (
    <main className={classes.counter}>
      <h1>Redux Counter</h1>
      {showCounter && <div className={classes.value}>{counterState}</div>}
      <div className={classes.counterBtn}>
        <button onClick={() => {handleIncrement()}}>+</button>
        <button onClick={() => {handleIncrement(5)}}>+5</button>
        <button onClick={() => {handleDecrement(5)}}>-5</button>
        <button onClick={() => {handleIncrement()}}>-</button>

      </div>
      <button onClick={toggleCounterHandler}>Toggle Counter</button>
    </main>
  );
};

export default Counter;
