        increaseBy (state,action) {
            state.counter += action.payload ;
        },
        decrease(state) {
            state.counter--;
        },
        decreaseBy(state, action) {
            if(!isNaN(action.payload)) {
                state.counter -= action.payload ;
            } else {
                return state.counter;
            }
            
        },
        toggleCounter(state) {
            state.showCounter = !state.showCounter;
