import redux from 'redux';

const CounterReducer = (state = {counter:0},action) => {

    if(action.type === 'increase') {
        return {
            counter: state.counter + 1,
            type: action.type
        }
    }
    if(action.type === 'decrease') {
        return {
            counter: state.counter - 1,
            type: action.type
        }
    }

    return state;
    
}
export const store = redux.createStore(CounterReducer);
